import React, { useState } from 'react';
import Adduser from './components/Adduser';
import Userlist from './components/Userlist';

function App(props) {
  const[adduserlist,setuserlist]=useState([])
 const Adduserhandler=(uName,uAge)=>{ //here uname is expect to get data and render to list here and also uage
      setuserlist((prevstatelist)=>{//here we are taking the old list and appending the element to the new list so we are using this anonymous function
        return[...prevstatelist,{name:uName,age:uAge,id:Math.random().toString()}]
      })
  }
  return (
    <div>
      <Adduser OnAddUser={Adduserhandler}/>
      <Userlist users={adduserlist}/> 

    </div>
  );
}//here the users are same as the users in the userlist because we are mappinng this array there with using props so if we didnt define here then mapping undefined is not possible and throw an error

export default App;
