import Card from "../UI/Card";
import classes from "./Adduser.module.css"
import { useState } from "react";
import Button from "../UI/Button";
const Adduser=()=>{
 const[enteredUsername,setUsername]=useState('')
 const[enteredAge,setUserage]=useState('')
    const Addhandler=(event)=>{
        event.preventDefault();//To ensure the page is not reloading after touching the button
    }
    const usernamehandler=(event)=>{
            setUsername(event.target.value)
    }
    const useragehandler=(event)=>{
        setUserage(event.target.value)
}
    return(
        <Card className={classes.input}>
        <form onSubmit={Addhandler}>
            <label htmlFor="Username" onChange={usernamehandler}>Username</label>
            <input id="Username" type="text"></input>
            <label htmlFor="Age" onChange={useragehandler}>Age(years)</label>
            <input  id="Age" type="number"></input>
            <Button>Submit</Button>

        </form>
        </Card>
        )
}
export default Adduser
