import React from 'react';

class TodoList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      newTask: '',
      newTaskDescription: '',
      searchTerm: '',
    };
  }

  componentDidMount() {
    const storedTasks = localStorage.getItem('tasks');
    if (storedTasks) {
      this.setState({ tasks: JSON.parse(storedTasks) });
    }
  }

  componentDidUpdate() {
    localStorage.setItem('tasks', JSON.stringify(this.state.tasks));
  }

  addTask = () => {
    if (this.state.newTask.trim() !== '') {
      const newTaskObj = {
        id: new Date().getTime(),
        title: this.state.newTask,
        description: this.state.newTaskDescription,
        completed: false,
      };
      this.setState((prevState) => ({
        tasks: [...prevState.tasks, newTaskObj],
        newTask: '',
        newTaskDescription: '',
      }));
    }
  };

  deleteTask = (taskId) => {
    this.setState((prevState) => ({
      tasks: prevState.tasks.filter((task) => task.id !== taskId),
    }));
  };

  toggleComplete = (taskId) => {
    this.setState((prevState) => ({
      tasks: prevState.tasks.map((task) => {
        if (task.id === taskId) {
          return {
            ...task,
            completed: !task.completed,
          };
        }
        return task;
      }),
    }));
  };

  handleSearch = (event) => {
    this.setState({ searchTerm: event.target.value });
  };

  render() {
    const filteredTasks = this.state.tasks.filter((task) =>
      task.title.toLowerCase().includes(this.state.searchTerm.toLowerCase())
    );

    return (
      <div>
        <h1>To-Do List</h1>
        <input
          type="text"
          placeholder="Search tasks"
          value={this.state.searchTerm}
          onChange={this.handleSearch}
        />
        <ul>
          {filteredTasks.map((task) => (
            <li key={task.id}>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => this.toggleComplete(task.id)}
              />
              <span className={task.completed ? 'completed' : ''}>
                {task.title}
              </span>
              <button onClick={() => this.deleteTask(task.id)}>Delete</button>
              <button>Edit</button>
              <p>{task.description}</p>
            </li>
          ))}
        </ul>
        <h2>Add New Task</h2>
        <input
          type="text"
          placeholder="Task title"
          value={this.state.newTask}
          onChange={(event) => this.setState({ newTask: event.target.value })}
        />
        <textarea
          placeholder="Task description"
          value={this.state.newTaskDescription}
          onChange={(event) =>
            this.setState({ newTaskDescription: event.target.value })
          }
        ></textarea>
        <button onClick={this.addTask}>Add Task</button>
      </div>
    );
  }
}

export default TodoList;
