import Card from "../UI/Card";
import classes from "./Adduser.module.css"
import { useState } from "react";
import Button from "../UI/Button";
import Errormodal from "../UI/Errormodal";
const Adduser = (props)=>{
 const[enteredUsername,setUsername]=useState('')
 const[enteredAge,setUserage]=useState('')
 const[error,seterror]=useState()
    const Addhandler=(event)=>{
        event.preventDefault();//To ensure the page is not reloading after touching the button
        if(enteredUsername.trim().length===0 || enteredAge.trim().length===0){
            seterror({
                title:"An error occured",message:"Please Enter valid input"
            })
            return ;//if i return somthing here then the execution will stop here itself so dont return here
        }
        if(+enteredAge.length<1){
            seterror({
                title:"An error occured",message:"Please Enter valid input"
            })
            return;
        }
        props.OnAddUser(enteredUsername,enteredAge)
        setUserage('')//once the form is submitted the form will resetted (only if we add the value prop in the input)
        setUsername('')
    }
    const usernamehandler=(event)=>{
            setUsername(event.target.value)//this the way that the user entered will be seen on the screen  
    }
    const useragehandler=(event)=>{
        setUserage(event.target.value)
    }
    const errorhandler=()=>{
        seterror( null)
    }
    return(
        <>
       {error && <Errormodal title={error.title} message={error.message} onConfirm={errorhandler}></Errormodal>}
        <Card className={classes.input}>
        <form onSubmit={Addhandler}>
            <label htmlFor="Username" >Username</label>
            <input id="Username" type="text" value={enteredUsername} onChange={usernamehandler}></input>
            <label htmlFor="Age" >Age(years)</label>
            <input  id="Age" type="number" value={enteredAge} onChange={useragehandler}></input>
            <Button type="submit">Add User</Button>

        </form>
        </Card>
        </>
        )//the type is very important here because this submit type triggers the onsubmit function
}
export default Adduser
